import { Component } from '@angular/core';
import {Customer} from "./app.model";

@Component({
  selector: 'app-root',
  templateUrl: './app.view.html',
})
export class CustomerComponent {
  title = 'Customer-UI';
  CustomerModel : Customer = new Customer();
  CustomerModels: Array<Customer> = new Array<Customer>();
  Add(){
    this.CustomerModels.push(this.CustomerModel);
    this.CustomerModel = new Customer();// clear the UI

  }
}
Customer Code :<input [(ngModel)]="CustomerModel.CustomerCode" type="text"/><br>
Customer Name :<input [(ngModel)]="CustomerModel.CustomerName" type="text"/><br>
Customer Amount :<input [(ngModel)]="CustomerModel.CustomerAmount" type="text"/><br>

<input (click)="Add" type="button" value="Add"/> <br>
{{CustomerModel.CustomerCode}}<br>
{{CustomerModel.CustomerName}}<br>
{{CustomerModel.CustomerAmount}}<br>

<table>
  <tr *ngFor="let cust in CustomerModels">
    <td>{{cust.CustomerCode}}</td>
    <td>{{cust.CustomerName}}</td>
    <td>{{cust.CustomerAmount}}</td>
  </tr>
</table>
