import { Component } from '@angular/core';
import {Customer} from "./app.model";

@Component({
  selector: 'app-root',
  templateUrl: './app.view.html',
})
export class CustomerComponent {
  title = 'Customer-UI';
  CustomerModel : Customer = new Customer();
  CustomerModels: Array<Customer> = new Array<Customer>();
  Add(){
    this.CustomerModels.push(this.CustomerModel);
    this.CustomerModel = new Customer();// clear the UI


  }
}

<div style="margin:auto; border: 5px solid; width: 20%;padding: 10px; background-color: aquamarine;">
  <h2 style="color:blue; text-align: center;">My Customer Application</h2>
    <div style="padding: 20px;">
      Customer Code : &nbsp; &nbsp; <input [(ngModel)]="CustomerModel.CustomerCode" type="text"/><br>
      Customer Name : &nbsp; &nbsp; <input [(ngModel)]="CustomerModel.CustomerName" type="text"/><br>
      Customer Amount : &nbsp; &nbsp; <input [(ngModel)]="CustomerModel.CustomerAmount" type="text"/><br>
    </div>


    <input (click)="Add()" type="button" value="Add"/> <br>
    {{CustomerModel.CustomerCode}}<br>
    {{CustomerModel.CustomerName}}<br>
    {{CustomerModel.CustomerAmount}}<br>


    <table>
      <th>Costumer Information</th>
      <tr >
        <td>CustomerCod</td>
        <td>CustomerName</td>
        <td>CustomerAmount</td>
      </tr>
      <tr *ngFor="let cust of CustomerModels">
        <td>{{cust.CustomerCode}}</td>
        <td>{{cust.CustomerName}}</td>
        <td>{{cust.CustomerAmount}}</td>
      </tr>
    </table>

</div>
